import React, { useState } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';

const items = [
  {
    src: './slide0.jpg',
    altText: 'AlgoTrader',
    caption: 'Cryptocurrency Trading Tool',
    capText: 'Make the best trades with your very own algorithmic trader'
  },
  {
    src: './slide01.jpg',
    altText: 'AlgoTrader',
    caption: 'Cryptocurrency Trading Tool',
    capText: 'Scanning the market on your behalf giving you freedom and wealth'
  },
  {
    src: './slide02.jpg',
    altText: 'AlgoTrader',
    caption: 'Cryptocurrency Trading Tool',
    capText: 'Scanning the market on your behalf giving you freedom and wealth'
  },
];

const Home = (props) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  }

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  }

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  }

  const slides = items.map((item) => {
    return (
      <CarouselItem
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}
        key={item.src}
      >
        <img src={item.src} width="100%" alt={item.altText} />
        <CarouselCaption captionText={item.capText} captionHeader={item.caption} />
      </CarouselItem>
    );
  });

  return (
    <Carousel
      activeIndex={activeIndex}
      next={next}
      previous={previous}
    >
      <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} />
      {slides}
      <CarouselControl direction="" directionText="" onClickHandler={previous} />
      <CarouselControl direction="" directionText="" onClickHandler={next} />
    </Carousel>
  );
}

export default Home;
